                    $new_data[]=array(
                        "name"=>$cart['name'],
                         "price"=>$cart['price'],
                         "img"=>$cart['img'],
                         "quantity"=>$cart['quantity'], 
                         "p_id"=>$data['id']
                     );
                }
                else $new_data[]=array("name"=>$cart['name'],"price"=>$cart['price'],"img"=>$cart['img'],"quantity"=>$cart['quantity'], );

            }
        }
        if(!$is_isset){
            if (isset($data['id']))
                $new_data[]=array("name"=>$name,"price"=>$price,"img"=>$img_src,"quantity"=>$quantity,"p_id"=>$data['id']);
            
            else $new_data[]=array("name"=>$name,"img"=>$img_src,"quantity"=>$quantity,);
        }
        $_SESSION['cart']=$new_data;

    }


}
else die();

